// Extract and format diff content for AI analysis
const diffData = $input.first().json;
const prData = $('Bitbucket Trigger').item.json;

// Parse diff content
let diffContent = '';
if (typeof diffData === 'string') {
  diffContent = diffData;
} else if (diffData.data) {
  diffContent = diffData.data;
} else {
  diffContent = JSON.stringify(diffData);
}

// Limit diff size for AI processing (max 6000 chars to be safe)
if (diffContent.length > 6000) {
  diffContent = diffContent.substring(0, 6000) + '\n... (diff truncated for AI analysis)';
}

// Extract PR metadata
const prInfo = {
  title: prData.pullrequest.title,
  description: prData.pullrequest.description || 'No description provided',
  author: prData.pullrequest.author.display_name,
  source_branch: prData.pullrequest.source.branch.name,
  destination_branch: prData.pullrequest.destination.branch.name,
  diff: diffContent,
  repository: prData.repository.full_name,
  pullrequest_id: prData.pullrequest.id,
  workspace: 'netpowerweb'
};

return { json: prInfo };

