// Process AI review response and extract key information
const aiResponse = $json.content;
const prData = $('Format Data for AI').item.json;

// Check for critical security issues
const securityKeywords = [
  'sql injection', 'xss', 'cross-site scripting', 'vulnerability', 
  'security risk', 'critical', 'exploit', 'unsafe', 'dangerous',
  'authentication', 'authorization', 'csrf', 'injection', 'exposure'
];

const lowerResponse = aiResponse.toLowerCase();
const hasCriticalIssues = securityKeywords.some(keyword => 
  lowerResponse.includes(keyword) && 
  (lowerResponse.includes('critical') || lowerResponse.includes('high') || lowerResponse.includes('vulnerability'))
);

// Extract priority level if mentioned
let priorityLevel = 'MEDIUM';
if (lowerResponse.includes('priority level')) {
  if (lowerResponse.includes('high')) priorityLevel = 'HIGH';
  else if (lowerResponse.includes('low')) priorityLevel = 'LOW';
}

// Create final review comment
const timestamp = new Date().toISOString();
const reviewComment = `# ü§ñ Automated Code Review (netpowerweb)

${aiResponse}

---

**PR Information:**
- **Repository:** ${prData.repository}
- **Author:** ${prData.author}
- **Branch:** \`${prData.source_branch}\` ‚Üí \`${prData.destination_branch}\`
- **Workspace:** ${prData.workspace}
- **Reviewed:** ${timestamp}
- **Priority:** ${priorityLevel}

${hasCriticalIssues ? 'üö® **ATTENTION:** Critical security issues detected!' : '‚úÖ No critical security issues detected'}

---

*This review was automatically generated by AI for the netpowerweb workspace. Please use as supplementary guidance alongside human code review.*`;

return {
  json: {
    review_comment: reviewComment,
    has_critical_issues: hasCriticalIssues,
    priority_level: priorityLevel,
    repository: prData.repository,
    pullrequest_id: prData.pullrequest_id,
    workspace: prData.workspace,
    ai_response: aiResponse
  }
};
